mapper.select:
		SELECT COUNT(*) AS C FROM  t_role 
		
			 
       			and id = #{id,jdbcType=INTEGER}
     		 
     	 	
      			and feedback = #{feedback,jdbcType=VARCHAR}
     		 
      		 
      			and userName = #{userName,jdbcType=VARCHAR}
     		 
      			 
     		 	and DATE_FORMAT(feedtime,'%Y-%m-%d %H:%i:%s')  
     		 	BETWEEN 
     		 	#{timesta,jdbcType=VARCHAR} 
     		 	AND 
     		 	#{timeend,jdbcType=VARCHAR}
     		 
     		  
     		 	and DATE_FORMAT(feedtime,'%Y-%m-%d') >=#{timesta,jdbcType=VARCHAR}
     		 
     		  
     		 	and DATE_FORMAT(feedtime,'%Y-%m-%d') <= #{timeend,jdbcType=VARCHAR}

		
		
			 
       			and id = #{param.id,jdbcType=INTEGER}
     		 
     	  	 
      			and feedback = #{param.feedback,jdbcType=VARCHAR}
     		 
      		 
      			and userName = #{param.userName,jdbcType=VARCHAR}
     		 
     		 
     		 	and DATE_FORMAT(feedtime,'%Y-%m-%d %H:%i:%s')  
     		 	BETWEEN 
     		 	#{param.timesta,jdbcType=VARCHAR} 
     		 	AND 
     		 	#{param.timeend,jdbcType=VARCHAR}
     		 
     		  
     		 	and DATE_FORMAT(feedtime,'%Y-%m-%d') >=#{param.timesta,jdbcType=VARCHAR}
     		 
     		  
     		 	and DATE_FORMAT(feedtime,'%Y-%m-%d') <= #{param.timeend,jdbcType=VARCHAR}
     		 
		
 		       limit ${start},${rows}
